hibernate-mapping.sql-query:

begin
	delete eo_issued_param eoip where eoip.pm_code in(:pmCodes) and eoip.org_id =:orgId ;
	delete eo_issued_condition eoic where eoic.eoic_eodp_pm_code in(:pmCodes) and eoic.org_id =:orgId ;
	delete eo_issued_rule eoir where eoir.eoir_eodp_pm_code in(:pmCodes) and eoir.org_id =:orgId ;
end;
