hibernate-mapping.sql-query:

  	declare
	begin
	  delete from MID_EO_ORDER_B2B_ITEM t
	   where t.edi_trans_id in (select a.edi_trans_id
	                              from MID_EO_ORDER_B2B a
	                             where a.job_id in (:jobIds));

	  delete from MID_EO_ORDER_B2B t where t.job_id in (:jobIds);
	end;
