hibernate-mapping.sql-query:

begin

DELETE FROM EO_ORDER_BUSINESS EOOB WHERE EOOB.EOOR_ORDER_NO IN (SELECT E.EOOR_ORDER_NO FROM EO_ORDER E WHERE E.EOOR_LINK_ORDER_NO IN (:linkOrderNOs) AND E.ORG_ID=:orgId) AND EOOB.ORG_ID=:orgId ;
DELETE FROM EO_ORDER EOOR WHERE EOOR.EOOR_LINK_ORDER_NO IN (:linkOrderNOs) and EOOR.ORG_ID=:orgId ;

end;
