hibernate-mapping.sql-query:


BEGIN
  DELETE EO_ORDER_MILESTONE EOOM WHERE EOOM.EOOM_ORDER_NO IN (:orderNos) AND EOOM.EOOM_WORK_NO IN (:workOrderNos) AND EOOM.ORG_ID=:orgId ;
  DELETE EO_WORK_ORDER EOWO WHERE EOWO.EOWO_ORDER_NO IN (:orderNos) AND EOWO.EOWO_WORK_ORDER_NO IN (:workOrderNos) AND EOWO.ORG_ID=:orgId ;
 END;
