mapper.select:
SELECT mogz.mogz_id, mogz.mogz_name, u.mvus_name AS creator, DATE_FORMAT( mogz.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, m.mvus_name AS modifier, DATE_FORMAT( mogz.modify_time, '%Y-%m-%d %H:%i:%s' ) AS modify_time FROM mv_organization mogz LEFT JOIN mv_user u ON u.mvus_id = mogz.creator LEFT JOIN mv_user m ON m.mvus_id = mogz.modifier WHERE 1 = 1
AND mogz.mogz_id = #{example.mogzId}
AND mogz.mogz_name LIKE CONCAT('%',#{example.mogzName},'%')
